package lab1;

public class QuotientAndRemainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 3, b=14;
		
		int quotient = b/a;
		
		int remainder = b%a;
		
		System.out.println("The quotient is: " + quotient);
		System.out.println("The remainder is: " + remainder);

	}

}
